import * as PIXI from 'pixi.js';

const dogImage = `data:image/jpeg;base64,`;

const seaImage = `data:image/png;base64,`;

function createGradTexture(maskWidth: number, gradWidth: number) {
  // adjust it if somehow you need better quality for very very big images
  const quality = 256;
  const canvas = document.createElement('canvas');
  canvas.width = quality;
  canvas.height = 1;

  const ctx = canvas.getContext('2d');
  if (!ctx) return;

  // use canvas2d API to create gradient
  const grd = ctx.createLinearGradient(0, 0, quality, 0);

  // grd.addColorStop(0, 'rgba(255, 255, 255, 0.0)');
  // grd.addColorStop(0.3, 'cyan');
  // grd.addColorStop(0.7, 'red');
  // grd.addColorStop(1, 'green');

  // grd.addColorStop(0.0, 'rgb(255,0,0)');
  // grd.addColorStop(0.5, 'rgb(0,255,0)');
  // grd.addColorStop(1.0, 'rgb(0,0,255)');

  // grd.addColorStop(0, 'rgba(255, 255, 255, 0.0)');
  // grd.addColorStop(0.4, 'rgba(255, 255, 255, 0.0)');
  // grd.addColorStop(0.6, 'rgba(255, 255, 255, 1.0)');
  // grd.addColorStop(1, 'rgba(255, 255, 255, 1.0)');

  // grd.addColorStop(0, 'rgba(255, 255, 255, 0.0)');
  // grd.addColorStop(0.15, 'rgba(255, 255, 255, 0.0)');
  // grd.addColorStop(0.33, 'rgba(255, 255, 255, 1.0)');
  // grd.addColorStop(1, 'rgba(255, 255, 255, 1.0)');

  // grd.addColorStop(0, 'rgba(255, 255, 255, 0.0)');
  // grd.addColorStop(0.165 - 0.0825, 'rgba(255, 255, 255, 0.0)');
  // grd.addColorStop(0.165 + 0.0825, 'rgba(255, 255, 255, 1.0)');
  // grd.addColorStop(0.33, 'rgba(255, 255, 255, 1.0)');

  const colorStopWidth = maskWidth / gradWidth;
  const center = colorStopWidth / 2;
  const halfRange = center / 2;

  grd.addColorStop(0, 'rgba(255, 255, 255, 0.0)');
  grd.addColorStop(center - halfRange, 'rgba(255, 255, 255, 0.0)');
  grd.addColorStop(center + halfRange, 'rgba(255, 255, 255, 1.0)');
  grd.addColorStop(colorStopWidth, 'rgba(255, 255, 255, 1.0)');

  ctx.fillStyle = grd;
  ctx.fillRect(0, 0, quality, 1);

  return PIXI.Texture.from(canvas);
}

function createDummyTexture() {
  // adjust it if somehow you need better quality for very very big images
  const quality = 256;
  const canvas = document.createElement('canvas');
  canvas.width = quality;
  canvas.height = 1;

  const ctx = canvas.getContext('2d');
  if (!ctx) return;
  // use canvas2d API to create gradient
  const grd = ctx.createLinearGradient(0, 0, quality, 0);

  const colorStopWidth = 1;
  const center = colorStopWidth / 2;
  const halfRange = center / 2;

  grd.addColorStop(0, 'black');
  grd.addColorStop(center - halfRange, 'white');
  grd.addColorStop(center + halfRange, 'white');
  grd.addColorStop(colorStopWidth, 'black');

  ctx.fillStyle = grd;
  ctx.fillRect(0, 0, quality, 1);

  return PIXI.Texture.from(canvas);
}

(() => {
  const canvas = document.querySelector('canvas.ex01') as HTMLCanvasElement;
  if (!canvas) return;
  const canvasSize = 500;

  const app = new PIXI.Application({
    width: canvasSize,
    height: canvasSize,
    view: canvas,
    backgroundColor: 0xf0f0f0,
  });

  const container = new PIXI.Container();
  app.stage.addChild(container);

  const graphics = new PIXI.Graphics();
  graphics.beginFill(0x557799, 1);
  graphics.drawRect(0, 0, 500, 500);
  graphics.endFill();
  container.addChild(graphics);

  const mask = new PIXI.Graphics();
  mask.beginFill(0xffffff, 0.1);
  const points = [
    new PIXI.Point(0, 0),
    new PIXI.Point(400, 0),
    new PIXI.Point(400, 400),
    new PIXI.Point(0, 400),
  ];
  mask.drawPolygon(points);
  mask.endFill();
  container.mask = mask;
})();

(() => {
  const canvas = document.querySelector('canvas.ex02') as HTMLCanvasElement;
  if (!canvas) return;
  const canvasSize = 500;

  const app = new PIXI.Application({
    width: canvasSize,
    height: canvasSize,
    view: canvas,
    backgroundColor: 0xf0f0f0,
  });

  const container = new PIXI.Container();
  app.stage.addChild(container);

  const seaWidth = 400;
  const seaHeight = 300;
  const seaX = 0;
  const seaY = 0;

  const dogWidth = 300;
  const dogHeight = 300;
  const dogX = 200;
  const dogY = 50;

  const seaContainer = new PIXI.Container();
  seaContainer.x = seaX;
  seaContainer.y = seaY;
  seaContainer.width = seaWidth;
  seaContainer.height = seaHeight;
  container.addChild(seaContainer);

  const seaSprite = PIXI.Sprite.from(seaImage);
  seaSprite.width = seaWidth;
  seaSprite.height = seaHeight;
  seaContainer.addChild(seaSprite);

  const dogContainer = new PIXI.Container();
  dogContainer.x = dogX;
  dogContainer.y = dogY;
  container.addChild(dogContainer);

  const dogSprite = PIXI.Sprite.from(dogImage);
  dogSprite.width = dogWidth;
  dogSprite.height = dogHeight;
  dogContainer.addChild(dogSprite);

  const maskWidth = seaWidth + seaX - dogX;
  const gradTexture = createGradTexture(maskWidth, dogWidth);
  const gradSprite = new PIXI.Sprite(gradTexture);
  gradSprite.width = dogWidth;
  gradSprite.height = dogHeight;
  dogContainer.mask = gradSprite;
  dogContainer.addChild(gradSprite);

  const dummy = createDummyTexture();
  const dummySprite = new PIXI.Sprite(dummy);
  dummySprite.x = dogX;
  dummySprite.y = 0;
  dummySprite.width = maskWidth;
  dummySprite.height = 50;
  dummySprite.alpha = 0.7;
  container.addChild(dummySprite);
})();
